sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	//Fält på Price Calculation formuläret som direkt kan visa vad OTC samt Sub pris blir.
	var otcPrice;
	var subPrice;

	var workCost;
	var unpackageCost;
	var theftmarkCost;
	var configurationCost;
	var carePackCost;
	var licenceCost;
	var accessoriesCost;
	var generalCost;
	var interestRate;
	var marginal;
	var invoiceFrequency;
	var months;
	var savingsDemand;
	var deviceCost;



	workCost = g_form.getIntValue('work_cost');
	unpackageCost = g_form.getIntValue('unpackage_cost');
	theftmarkCost = g_form.getIntValue('theftmark_cost');
	configurationCost = g_form.getIntValue('configuration_cost');
	carePackCost = g_form.getIntValue('care_pack_cost');
	licenceCost = g_form.getIntValue('licence_cost');
	accessoriesCost = g_form.getIntValue('accessories_cost');
	generalCost = g_form.getIntValue('general_cost');
	interestRate = g_form.getValue('interest_rate');
	marginal = g_form.getValue('marginal');
	invoiceFrequency = g_form.getValue('invoice_frequency');
	months = g_form.getValue('months');
	deviceCost = g_form.getIntValue('device_cost');
	savingsDemand = g_form.getValue('add_savings_demand');


	otcPrice = deviceCost + +workCost + +unpackageCost + +theftmarkCost + +configurationCost + +carePackCost + +licenceCost + +accessoriesCost + +generalCost;

	var marginalSummary = 0;
	var interestRateSummary = 0;

	marginalSummary += deviceCost;
	marginalSummary *= marginal;

	interestRateSummary += deviceCost;
	interestRateSummary *= interestRate;
	interestRateSummary -= deviceCost;

	subPrice = +otcPrice + +marginalSummary + +interestRateSummary;
	subPrice *= invoiceFrequency;
	subPrice /= months;


	if (savingsDemand == 'true') {
		var savingscost = 0;
		savingscost = subPrice * (months/12) / months;
		subPrice += savingscost;
	}


	otcPrice = Math.ceil(otcPrice / 5) * 5;
	subPrice = Math.ceil(subPrice / 5) * 5;


	g_form.setValue('otc_price', parseFloat(otcPrice).toString());
	g_form.setValue('sub_price', parseFloat(subPrice).toString());

}
