sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	//Klickar man på "Use calculated price" för ett catalog item så hämtas priserna från kopplad Model. Fungerar även för Consumables.
	var productModel = g_form.getValue('model');
	var isConsumable = g_form.getDisplayBox('u_model_category').value;


	if (newValue == 'true' && isConsumable != 'Consumable'){


		var ga = new GlideAjax('ModelUtil');  //Script include: ModelUtil
		ga.addParam('sysparm_name', 'getPrices');
		ga.addParam('sysparm_sysid', productModel);
		ga.getXMLAnswer(getModelPrices);
	}

	else if(newValue == 'true' && isConsumable == 'Consumable'){

		var gaConPrice = new GlideAjax('ConsumablePrice');  //Script include: ConsumablePrice
		gaConPrice.addParam('sysparm_name', 'getPrices');
		gaConPrice.addParam('sysparm_sysid', productModel);
		gaConPrice.getXMLAnswer(getConsumablePrices);
	}

	else {
		g_form.setReadOnly('price', false);
		g_form.setReadOnly('recurring_price', false);
		g_form.setValue('price', '0');
		g_form.setValue('recurring_price', '0');
	}
}

function getModelPrices(response) {
	response = JSON.parse(response);

	var salesModel = g_form.getValue('u_sales_model');

	if (salesModel == 'One-time charge') {
		g_form.setValue('price', response.otcPrice);
	}
	if (salesModel == 'Subscription') {
		g_form.setValue('recurring_price', response.subPrice);
	}
}

function getConsumablePrices(response) {
	response = JSON.parse(response);

	var salesModel = g_form.getValue('u_sales_model');
	var cPriceSubCharge = parseInt(response.ConsumablePrice, 10);
	cPriceSubCharge *= 1.15; //påslag på 15% på tillbehör när de säljs i portalen
	cPriceSubCharge = Math.round(cPriceSubCharge, 0);
	var cPrice = cPriceSubCharge.toString();


	if (salesModel == 'One-time charge') {
		g_form.setValue('price', cPrice);
	}
	else if(salesModel == 'Subscription'){
		g_form.setValue('recurring_price', 0);
	}

}
