sys_script_include.script:
var ModelUtil = Class.create();
ModelUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getPrices: function() {

		var model = this.getParameter('sysparm_sysid');

		var gr = new GlideRecord('cmdb_hardware_product_model');
		var answer = {};
		if (gr.get(model)) {
			answer.otcPrice = gr.getValue('x_hak_price_calc_one_time_charge_price');
			answer.subPrice = gr.getValue('x_hak_price_calc_subscription_cost');
		}

		return JSON.stringify(answer);
	},


    type: 'ModelUtil'
});
